public class IfElseExample {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// if 50 or above
		// if 50 or below  fail
		
		
		int mark, passingGrade;
		mark = 40;
		passingGrade = 50;
		
		
		if (mark >= passingGrade) {
			System.out.println("MSc pass");
		} else {
			System.out.println("MSc fail");
		}
		
		
		
		System.out.println("Done");
		
		
		}
	}

