package compoundsAndLoops;

/**
 * 
 */
public class PreAndPostFix {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method 
		int num;
		num = 1;
		
		
		System.out.println(++num); // 2
		System.out.println(num++); // 2
		System.out.println(num); // 3

	}

}
