package loopsLabExercises;

/**
 * for loops
 */
public class Part3Q5 {

	/**
	 * output even numbers between 1 - 100
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		for (int i = 1 ; i<=100; i++) {
			if (i % 2 == 0) {
				System.out.println(i);
			}
		}
	}

}
